import React, {Component} from 'react';
import {BrowserRouter as Router, Route,Link,Redirect } from 'react-router-dom';
import './search.css';
import axios from 'axios';

export default class order extends Component {
	constructor(props){
		super(props);

		this.findProducts = this.findProducts.bind(this);
		this.onChange = this.onChange.bind(this);

		this.state = {
			name: "",
			products: [],
			html1: "",
			sort: "price",
			errors: {}
		};
	}

	componentDidMount(){
		axios.get('http://localhost:4000/product/')
			.then(res => {
				console.log(res.data);
				this.setState({
					products: res.data
				});
			})
			.catch(err => {
				console.log(err);
			})
	}

	findProducts(wordToMatch){
		return this.state.products.filter(prod => {
			const reg = new RegExp(wordToMatch,'gi');
			return prod.name.match(reg);
		})
	}

	onChange = e => {

		this.setState({
			[e.target.id]: e.target.value
		});

	};

	productsList(){
		const pros = this.findProducts(this.state.name);
		console.log(pros);
		console.log(this.state.sort);
		pros.sort(function(a,b){
			if(this.state.sort==="price")
				return Number(a.price)-Number(b.price);
			else if(this.state.sort=="qleft"){
				const x1=Number(a.quantity)-Number(a.order);
				const x2=Number(b.quantity)-Number(b.order);
				return x1-x2;
			}
			else{
				return a.name-b.name;
			}
		})
		console.log(pros);
		let search=[]
		pros.map(prod => {
			const regex = new RegExp(this.state.name,'gi');
			const qleft=Number(prod.quantity)-Number(prod.order);
			var currentitem={};
			currentitem.name=prod.name;
			currentitem.vname=prod.vendorname;
			currentitem.price=prod.price;
			currentitem.qleft = qleft;
			console.log(currentitem);
			search.push(<li>
						<span style={{fontSize: "15px",width:"30%"}}>{currentitem.name}</span>
						<span style={{ fontSize: "16px",width:"30%"}}>{currentitem.vname}</span>
						<span style={{ fontSize: "17px",width:"25%"}}>{currentitem.price}</span>
						<span style={{ fontSize: "18px",width:"15%"}}>{currentitem.qleft}</span>
						</li>)
		});
		return search;
	}

	render(){
		return(
			<Router>
			<div style={{textAlign:"right",fontSize:"40px",fontFamily:"Courier New",color:"blue"}}>Hello {this.props.match.params.id}</div>
				<div className="input-field col s12" style={{float:"left"}}>
					<label style={{marginTop:"-6.3rem"}}>Sort By: </label>
					<select
						value={this.state.sort}
						onChange={this.onChange}
						id="sort"
						default="added date"
						style={{width:"25%",marginTop:"-4rem"}}
						className="form-control">
						<option value="price">Price</option>
						<option value="qleft">Quantity left</option>
						<option value="rating">Rating</option>
					</select>
				</div>
				<form className="search-form" style={{maxWidth: "400px",margin:"50px auto"}}>
					<input 
						type="text"  
						id="name"
						value= {this.state.name}
						onChange= {this.onChange}
						placeholder ="Product Name"
						style={{
							margin: "0px",
							textAlign: "center",
							outline: "0px",
							border: "10px solid #F7F7F7",
							width: "120%",
							left: "-10%",
							position: "relative",
							top: "10px",
							zIndex: "2",
							borderRadius: "5px",
							fontSize: "30px",
							boxShadow: "0 0 5px rgba(0, 0, 0, 0.12), inset 0 0 2px rgba(0, 0, 0, 0.19)",
						}}
					/>
					<ul className="suggestions">
						<li>
							<span style={{fontSize: "15px",width:"30%"}}>Product Name </span>
							<span style={{ fontSize: "16px",width:"30%"}}>Vendor</span>
							<span style={{ fontSize: "17px",width:"25%"}}>Price</span>
							<span style={{ fontSize: "18px",width:"15%"}}>Quantity Left</span>
						</li>
					</ul>
					<ul className="suggestions">
						{this.productsList()}
					</ul>
				</form>
			</Router>
		)
	}
}
