import React, {Component} from 'react';
import {BrowserRouter as Router, Route,Link,Redirect } from 'react-router-dom';
import './search.css';
import axios from 'axios';

export default class order extends Component {
	constructor(props){
		super(props);

		this.findProducts = this.findProducts.bind(this);
		this.onChange = this.onChange.bind(this);

		this.state = {
			name: "",
			products: [],
			html1: "",
			errors: {}
		};
	}

	componentDidMount(){
		axios.get('http://localhost:4000/product/')
			.then(res => {
				console.log(res.data);
				this.setState({
					products: res.data
				});
			})
			.catch(err => {
				console.log(err);
			})
	}

	findProducts(wordToMatch){
		return this.state.products.filter(prod => {
			const reg = new RegExp(wordToMatch,'gi');
			return prod.name.match(reg);
		})
	}

	onChange = e => {

		this.setState({
			[e.target.id]: e.target.value
		});

	};

	productsList(){
		const pros = this.findProducts(this.state.name);
		console.log(pros);
		console.log("mayank");

		let search=[]
		pros.map(prod => {
			const regex = new RegExp(this.state.name,'gi');
			const qleft=Number(prod.quantity)-Number(prod.order);
			var currentitem={};
			currentitem.name=prod.name;
			currentitem.vname=prod.vendorname;
			currentitem.price=prod.price;
			currentitem.qleft = qleft;
			console.log(currentitem);
			search.push(<li>
						<span style={{fontSize: "15px" }}>{currentitem.name}</span>
							<span style={{ fontSize: "18px" }}>{currentitem.vname},{currentitem.price},{currentitem.qleft}</span>
						</li>)
		});
		return search;
	}

	render(){
		return(
			<Router>
				<form className="search-form" style={{maxWidth: "400px",margin:"50px auto"}}>
					<input 
						type="text"  
						id="name"
						value= {this.state.name}
						onChange= {this.onChange}
						placeholder ="Product Name"
						style={{
							margin: "0px",
							textAlign: "center",
							outline: "0px",
							border: "10px solid #F7F7F7",
							width: "120%",
							left: "-10%",
							position: "relative",
							top: "10px",
							zIndex: "2",
							borderRadius: "5px",
							fontSize: "30px",
							boxShadow: "0 0 5px rgba(0, 0, 0, 0.12), inset 0 0 2px rgba(0, 0, 0, 0.19)",
						}}
					/>
					<ul className="suggestions">
						<li>
							<span style={{fontSize: "15px"}}>Product Name </span>
							<span style={{ fontSize: "18px" }}>Vendor , Price ,Quantity Left</span>
						</li>
					</ul>
					<ul className="suggestions">
						{this.productsList()}
					</ul>
				</form>
			</Router>
		)
	}
}
